<template>
    <div id="template" v-show="power">
        <div v-if="timeLeft > 0">
            <div id="countdown">
                <div id="countdown-number">{{ timeLeft }}</div>
                <svg>
                    <circle r="6.75vh" cx="7.5vh" cy="7.5vh"></circle>
                </svg>
            </div>
            <h3>Bist du noch da?</h3>
            <p>Bitte bestätige, dass du noch anwesend bist, andernfalls wirst du ausgeloggt!</p>
            <button class="btn primary" v-on:click="dontAFK">Ich bin noch da</button>
            <audio v-if="power == true" autoplay controls type="audio/ogg" preload="auto" src="data:audio/ogg;base64,"/>
        </div>
        <div v-else>
            <h1>Du wurdest ausgeloggt!</h1>
            <h2>Aufgrund das du längere Zeit AFK warst, wurdest du automatisch ausgeloggt.</h2>
            <h2>Um wieder auf die Insel einzureisen musst du GTA neu starten und dich neu Whitelisten.</h2>
        </div>
    </div>
</template>

<script>
import Components from "../components"
var AFKtimer;
export default Components.register({
    name: "AntiAFK",
    props: {
        data: String
    },
    data() {
        return {
            power: false,
            timeLeft: 60
        }
    },
    methods: {
        checkAFK(){
            if(this.power === true){
                this.timeLeft = this.timeLeft - 1
                if(this.timeLeft == 0){
                    this.triggerServer("kick")
                }
            }
        },
        dontAFK(){
            this.power = false
            this.timeLeft = 60
            this.trigger("disableAntiAFK",[])
        }
    },
    mounted(){
        AFKtimer = setInterval(this.checkAFK,1000)
    }

})
</script>

