<template>
    <div class="vehicleHud" v-show="activeTacho">
        <div id="vehicle-bar">
            <audio v-for="x in playmusic"  v-bind:key="x" visibility="visible" currentTime="0" autoplay controls type="audio/ogg" preload="auto" src="data:audio/ogg;base64,"/>
            <ul class="bar-itemlist" v-bind:class="{ 'bar-on': engine == true, 'bar-off': engine == false}">
                <li class="bar-item first">
                    <img src="../../assets/hud_icons/engine-off.png" class="bar-item-image small" v-if="engine == false">
                    <img src="../../assets/hud_icons/engine-on.png" class="bar-item-image small" v-if="engine == true || engine == null">
                </li>
                <li class="bar-item">
                    <img src="../../assets/hud_icons/locked.png" class="bar-item-image small" v-if="lock == true">
                    <img src="../../assets/hud_icons/not-locked.png" class="bar-item-image small" v-if="lock == false">
                </li>
                <!--
                <li class="bar-item">
                    <img src="../../assets/hud_icons/seatbelt-locked.png" class="bar-item-image small" v-if="seatbelt == true">
                    <img src="../../assets/hud_icons/seatbelt.svg" class="bar-item-image small" v-if="seatbelt == false">
                </li>
                -->
                <li class="bar-item">
                    <img src="../../assets/hud_icons/speedometer.svg" class="bar-item-image small" v-show="!tempomat">
                    <img src="../../assets/hud_icons/speedometer_tempo.svg" class="bar-item-image small" v-show="tempomat">
                    <span>{{speed}} KM/H</span>
                </li>
                <li class="bar-item"><img src="../../assets/hud_icons/gas.svg" class="bar-item-image small"> <span v-bind:style="{ color: activeColor}">{{Number(fuel).toFixed(2)}} l</span></li>
                <!--<li class="bar-item"><img src="../../assets/hud_icons/medical-bag.svg" class="bar-item-image"> <span>{{health}}%</span></li>-->
                <li class="bar-item"><img src="../../assets/hud_icons/distance.svg" class="bar-item-image small"> <span>{{Number(mileage).toLocaleString('de')}} km</span></li>
            </ul>
        </div>
    </div>
</template>

<script>
    import Components from '../components'
    var timerFuel;

    export default Components.register({
        name: "VehiclePanel",
        data() {
            return {
                activeTacho: false,
                tempomat:false,
                speed: 0,
                fuel: 1,
                health: 0,
                engine: false,
                seatbelt: false,
                lock: false,
                handBreak: false,
                mileage: 0,
                maxspeed: 500,
                maxfuel: 0,
                activeColor: "white",
                alarm: false,
                playmusic: 0
            }
        },
        methods: {
            responseVehicleSpeed: function (vehicleSpeed) {
                this.speed = vehicleSpeed
            },
            alarmFuel(){
                if(this.fuel <= this.maxfuel/10 && this.engine == true && this.activeTacho == true){
                    if(this.activeColor == "white"){
                        this.activeColor = "red"
                        this.playmusic++
                    }else if(this.activeColor == "red"){
                        this.activeColor = "white"
                    }
                }
                else{
                    this.playmusic = 0
                    this.activeColor = "white"
                }
            },
            log: function (engineState) {
                console.log(engineState)
            }
        },
        mounted()
        {
            timerFuel = setInterval(this.alarmFuel,1000)
        }
    })
</script>

